processors.comments:
New, with tolower transform

[
  {
    "operation": "shift",
    "spec": {
      "country": "country",
      "applications": {
        "*": { // state array index
          "applicationId": "applications[&1].applicationId",
          "attributes": "applications[&1].attributes",
          "attributes.diagnostics": "applications[&1].attributes",
          "mr2AppInformation": "applications[&1].mr2AppInformation",
          "name": "applications[&1].name",
          "startTime": "applications[&1].startTime",
          "user": "applications[&1].user",
          "pool": "applications[&1].pool",
          "state": "applications[&1].state",
          "progress": "applications[&1].progress",
          "applicationTags": "applications[&1].applicationTags",
          "allocatedMemorySeconds": "applications[&1].allocatedMemorySeconds",
          "allocatedVcoreSeconds": "applications[&1].allocatedVcoreSeconds",
          "allocatedMB": "applications[&1].allocatedMB",
          "allocatedVCores": "applications[&1].allocatedVCores",
          "runningContainers": "applications[&1].runningContainers"
        }
      }
    }
  },{
    // unwrap the keys and values into literal
    // "key" : "A", "value" : "b"
    "operation": "shift",
    "spec": {
      "*": {
        "$": "&1.key",
        "@": "&1.value"
      },
      //do the same for everything in aliases
      //&3 = applications
      //&2 = array position
      //&1 = position of kvp
      "applications": {
        "*": {
          "*": {
            "$": "&3.&2.&1.key",
            "@": "&3.&2.&1.value"
          }
        }
      }
    }
  },
  {
    "operation": "modify-overwrite-beta",
    "spec": {
      "*": {
        // Now that the origional key
        //  is on the "right hand side"
        //  lowercase it
        "key": "=toLower"
      },
      "applications": {
        "*": {
          "*": {
            // Now that the origional key
            //  is on the "right hand side"
            //  lowercase it
            "key": "=toLower"
          }
        }
      }
    }
  },
  {
    // pivot back, the now lowercased keys
    "operation": "shift",
    "spec": {
      "*": {
        "value": "@(1,key)"
      },
      "applications": {
        "*": {
          "*": {
            //&3 = applications
            //&2 = array postion
            //@(1,key) values from "key"
            "value": "&3.[&2].@(1,key)"
          }
        }
      }
    }
  }
]


